import React, { useState, useEffect } from 'react'
import { motion } from 'framer-motion'
import images from '../../constants/images'
import './About.scss'
const abouts = [
      { title: 'Web Development', description: 'I am a good Web Developer.', imgUrl: images.about01 },
      { title: 'Frontend Development', description: 'I am a good Web Developer.', imgUrl: images.about02 },
      { title: 'Web UI/UX', description: 'I am a good Web Developer.', imgUrl: images.about03 },
      { title: 'MERN Stack', description: 'I am a good Web Developer.', imgUrl: images.about04 }
]
const About = () => {
      return (
            <>
                  <h2 className='head-text'>
                        I know That
                        <span>Good Apps</span>
                        <br />
                        <span>Good Business</span>
                  </h2>
                  <div className="app__profiles">
                        {
                              abouts.map((about, index) => (
                                    <motion.div
                                          whileInView={{ opacity: 1 }}
                                          whileHover={{ scale: 1.1 }}
                                          transition={{ duration: 0.5, type: 'spring' }}
                                          className="app__profile-item"
                                          key={about.title + index}
                                    >
                                          <img src={about.imgUrl} alt={about.title} />
                                          <h2 className='bold-text' style={{ marginTop: 20 }}>{about.title}</h2>
                                          <p className='p-text' style={{ marginTop: 10 }}>{about.description}</p>
                                    </motion.div>
                              ))
                        }
                  </div>
            </>
      )
}

export default About
